const { Parallax, ParallaxLayer } = require('react-spring/addons.cjs')
import Sketch from "react-p5";

export class App extends React.Component {
	x = 50;
	y = 50;

	setup = (p5, canvasParentRef) => {
		p5.createCanvas(500, 500).parent(canvasParentRef); // use parent to render canvas in this ref (without that p5 render this canvas outside your component)
	};
	draw = p5 => {
		p5.background(0);
		p5.ellipse(this.x, this.y, 70, 70);
		// NOTE: Do not use setState in draw function or in functions that is executed in draw function... pls use normal variables or class properties for this purposes
		this.x++;
	};
	constructor(props) {
		super(props);
	}
	render () {
		return (
			<Parallax pages={4} scrolling={true} horizontal ref={ref => (this.parallax = ref)}>
				{/* PAGE 1 */}
				<ParallaxLayer 
					offset={0}
					speed={0.5}
					style={{ display: 'flex', alignItems: 'center', justifyContent: 'center' }}
					onClick={() => this.parallax.scrollTo(1)}>

					<Sketch setup={this.setup} draw={this.draw} />	
					<img width="400em" src={'../static/placeholder.png'}/>
				</ParallaxLayer>
				<ParallaxLayer 
					offset={0.23}
					speed={.75}
					onClick={() => this.parallax.scrollTo(1)}
					style={{marginTop: '3em'}}>
				<img width="200" src={'../static/blob-shape (3).svg'}/>
				</ParallaxLayer> 
				<ParallaxLayer 
					offset={.58}
					speed={1}
					style={{marginTop: '23em'}}
					onClick={() => this.parallax.scrollTo(1)}>
					<p> This is the first product. </p>
				</ParallaxLayer>
				{/* PAGE 2 */}
				<ParallaxLayer 
					offset={1}
					speed={0.5}
					style={{ display: 'flex', alignItems: 'center', justifyContent: 'center' }}
					onClick={() => this.parallax.scrollTo(2)}>
						<img width="400em" src={'../static/placeholder.png'}/>
				</ParallaxLayer>
				<ParallaxLayer 
					offset={1.58}
					speed={.75}
					onClick={() => this.parallax.scrollTo(2)}
					style={{marginTop: '3em'}}>
				<img width="200" src={'../static/blob-shape (4).svg'}/>
				</ParallaxLayer> 
				<ParallaxLayer 
					offset={1.23}
					speed={1}
					style={{marginTop: '23em'}}
					onClick={() => this.parallax.scrollTo(2)}>
					<p> This is the second product. </p>
				</ParallaxLayer>			
				{/* PAGE 3 */}
				<ParallaxLayer 
					offset={2}
					speed={0.5}
					style={{display: 'flex', alignItems: 'center', justifyContent: 'center' }}
					onClick={() => this.parallax.scrollTo(0)}>
						<img width="400em" src={'../static/placeholder.png'}/>
				</ParallaxLayer>
				<ParallaxLayer 
					offset={2.23}
					speed={.75}
					style={{zIndex: '1', marginTop: '3em'}}
					onClick={() => this.parallax.scrollTo(0)}>
				<img width="200" src={'../static/blob-shape (3).svg'}/>
				</ParallaxLayer> 
				<ParallaxLayer 
					offset={2.58}
					speed={1}
					style={{zIndex: '2', marginTop: '23em'}}
					onClick={() => this.parallax.scrollTo(0)}>
					<p> This is the third product. </p>
				</ParallaxLayer>	



				<ParallaxLayer 
					offset={2}
					speed={0.5}
					style={{ display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
					<span onClick={() => this.parallax.scrollTo(0)}>
						<img width="400em" src={'../static/placeholder.png'}/>
					</span>
				</ParallaxLayer>
			</Parallax>

			)
}
}

export default App;
