const { Parallax, ParallaxLayer } = require('react-spring/addons.cjs');
import dynamic from 'next/dynamic';

export class App extends React.Component {
	constructor(props) {
		super(props);
		this.p5Ref = React.createRef()
		this.noiseRef = React.createRef()
  	this.state = {
   		x: 100,
  		y: 100
    }
	}

	componentDidMount() {
		const p5 = require("p5");
		require('p5/lib/addons/p5.sound');
		this.noise_sketch = new p5( p => {
			let noiseScale = 0.02;	
			p.setup = ()  => {
				p.createCanvas(p.windowWidth, p.windowHeight)
					.parent(this.noiseRef.current);
				p.background('white');
				p.strokeWeight(5);
			}
			p.draw = () => {
				p.background(255);
  			for (let x=0; x < p.width; x++) {
					let noiseVal = p.noise((p.mouseX+x)*noiseScale, p.mouseY*noiseScale);
					let c1 = 202*noiseVal
					let c2 = 217*noiseVal
					let c3 = 216*noiseVal
					p.stroke(c1, c2, c3);
    			p.line(x, p.mouseY+noiseVal*80, x, p.height);
				}
			}
		})
		this.sketch = new p5( p => {
			let mySound, mouseMoved = "";
			let locs = [];

		p.setup = () => {
  		p.createCanvas(p.windowWidth, p.windowHeight);
			var res = 20;
  		var countX = p.ceil(p.width/res) + 1;
  		var countY = p.ceil(p.height/res) + 1;

  		for (var j = 0; j < countY; j++) {
    		for (var i = 0; i < countX; i++) {
      		locs.push( new p5.Vector(res*i, res*j) );
    		}
  		};
			p.noFill();
  	p.stroke(249,78,128);
	}

	p.draw = () => {
  	p.background(30,67,137);
  	for (var i = locs.length - 1; i >= 0; i--) {
    	var h = calcVec( locs[i].x - mouseX, locs[i].y - mouseY);
    	line(
      	locs[i].x, 
      	locs[i].y, 
      	locs[i].x + 15*cos(h.heading()), 
      	locs[i].y + 15*sin(h.heading())
    	);
  };
}

function calcVec(x, y) {
  return new p5.Vector(y - x, - x - y);
}			
	/*p.preload = () => {
				p.soundFormats('mp3')
				mySound = p.loadSound('../static/apple.mp3')
			}
			p.setup = ()  => {
				p.createCanvas(p.windowWidth, p.windowHeight)
					.parent(this.p5Ref.current);
				p.background('white');
				p.strokeWeight(5);


			}
			p.windowResized = () => {
				p.resizeCanvas(p.windowWidth, p.windowHeight )
			}
			p.mouseMoved = () => {
				mouseMoved = (p.mouseX / p.width - 0.5) * 4
				if(mouseMoved > 0) {
					console.log("play sound 1")
				} else {
					console.log("play sound 2")
				}
			}	
			p.draw = () => {
				if(p.mouseIsPressed){
					p.stroke(225)
				}else{
					p.stroke(0, 0, 0);
				}
				p.line(p.mouseX , p.mouseY, p.mouseX , p.mouseY);
				p.line(p.mouseX, p.mouseY , p.mouseX, p.mouseY);
			}*/
		});
}

	render () {
		return (
			<Parallax pages={4} scrolling={true} horizontal ref={ref => (this.parallax = ref)}>
				{/* PAGE 1 */}
				<ParallaxLayer 
					offset={0}
					speed={0.5}
					style={{ display: 'flex', alignItems: 'center', justifyContent: 'center' }}
					onClick={() => this.parallax.scrollTo(1)}>
						<div className="p5">
    					<div ref={this.p5Ref}></div>
  				</div>
				</ParallaxLayer>
				<ParallaxLayer 
					offset={0.23}
					speed={.75}
					onClick={() => this.parallax.scrollTo(1)}
					style={{marginTop: '3em'}}>
				<img width="200" src={'../static/blob-shape (3).svg'}/>
				</ParallaxLayer> 
				<ParallaxLayer 
					offset={.58}
					speed={1}
					style={{marginTop: '23em'}}
					onClick={() => this.parallax.scrollTo(1)}>
					<p> This is the first product. </p>
				</ParallaxLayer>
				{/* PAGE 2 */}
				<ParallaxLayer 
					offset={1}
					speed={0.5}
					style={{ display: 'flex', alignItems: 'center', justifyContent: 'center' }}
					onClick={() => this.parallax.scrollTo(2)}>
						<div className="p5">
    					<div ref={this.noiseRef}></div>
  				</div>
				</ParallaxLayer>
				<ParallaxLayer 
					offset={1.58}
					speed={.75}
					onClick={() => this.parallax.scrollTo(2)}
					style={{marginTop: '3em'}}>
				<img width="200" src={'../static/blob-shape (4).svg'}/>
				</ParallaxLayer> 
				<ParallaxLayer 
					offset={1.23}
					speed={1}
					style={{marginTop: '23em'}}
					onClick={() => this.parallax.scrollTo(2)}>
					<p> This is the second product. </p>
				</ParallaxLayer>			
				{/* PAGE 3 */}
				<ParallaxLayer 
					offset={2}
					speed={0.5}
					style={{display: 'flex', alignItems: 'center', justifyContent: 'center' }}
					onClick={() => this.parallax.scrollTo(0)}>
						<img width="400em" src={'../static/placeholder.png'}/>
				</ParallaxLayer>
				<ParallaxLayer 
					offset={2.23}
					speed={.75}
					style={{zIndex: '1', marginTop: '3em'}}
					onClick={() => this.parallax.scrollTo(0)}>
				<img width="200" src={'../static/blob-shape (3).svg'}/>
				</ParallaxLayer> 
				<ParallaxLayer 
					offset={2.58}
					speed={1}
					style={{zIndex: '2', marginTop: '23em'}}
					onClick={() => this.parallax.scrollTo(0)}>
					<p> This is the third product. </p>
				</ParallaxLayer>	



				<ParallaxLayer 
					offset={2}
					speed={0.5}
					style={{ display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
					<span onClick={() => this.parallax.scrollTo(0)}>
						<img width="400em" src={'../static/placeholder.png'}/>
					</span>
				</ParallaxLayer>
			</Parallax>

			)
}
}

export default App;
