const { Parallax, ParallaxLayer } = require('react-spring/addons.cjs');
import dynamic from 'next/dynamic';

export class App extends React.Component {
	constructor(props) {
		super(props);
		this.p5Ref = React.createRef()
		this.noiseRef = React.createRef()
  	this.state = {
   		x: 100,
  		y: 100
    }
	}

	componentDidMount() {
		const p5 = require("p5");
		require('p5/lib/addons/p5.sound');
		this.noise_sketch = new p5( p => {
			let noiseScale = 0.02;	
			p.setup = ()  => {
				p.createCanvas(p.windowWidth, p.windowHeight)
					.parent(this.noiseRef.current);
				p.background('white');
				p.strokeWeight(5);
			}
			p.draw = () => {
				p.background(255);
				for (let x=0; x < p.width; x++) {
					let noiseVal = p.noise((p.mouseX+x)*noiseScale, p.mouseY*noiseScale);
					let c1 = 202*noiseVal
					let c2 = 217*noiseVal
					let c3 = 216*noiseVal
					p.stroke(c1, c2, c3);
					p.line(x, p.mouseY+noiseVal*80, x, p.height);
				}
			}
		})

		this.sketch = new p5( p => {
			let osc, envelope, mySound, mouseMoved = "";
			let locs = [];

			p.setup = () => {
				p.createCanvas(400, 400)
					.parent(this.p5Ref.current);
				var res = 20;
				var countX = p.ceil(p.width/res) + 1;
				var countY = p.ceil(p.height/res) + 1;
				osc = new p5.SinOsc();
				envelope = new p5.Env();
				envelope.setADSR(0.001, 0.5, 0.1, 0.5);
				envelope.setRange(1, 0);
				osc.start();

				for (var j = 0; j < countY; j++) {
					for (var i = 0; i < countX; i++) {
						locs.push( new p5.Vector(res*i, res*j) );
					}
				};
				p.noFill();
				p.stroke(249,78,128);
			}
			p.windowResized = () => {
				p.resizeCanvas(p.windowWidth, p.windowHeight )
			}
			p.mouseMoved = () => {
				p.userStartAudio()
				mouseMoved = (p.mouseX / p.width - 0.5) * 4
				if(mouseMoved < 1 && mouseMoved > 0.5) {
				    let freqValue = p.midiToFreq(60);
				    osc.freq(freqValue);
				    envelope.play(osc, 0, 0.1);
				} 
				if(mouseMoved > 0 && mouseMoved < 0.5) {
				    let freqValue = p.midiToFreq(62);
				    osc.freq(freqValue);
				    envelope.play(osc, 0, 0.1);
				}
				if(mouseMoved < 0 && mouseMoved < -(0.5)) {
				    let freqValue = p.midiToFreq(64);
				    osc.freq(freqValue);
				    envelope.play(osc, 0, 0.1);
				}
				if(mouseMoved > -(1.0) && mouseMoved < -(0.5)) {
				    let freqValue = p.midiToFreq(67);
				    osc.freq(freqValue);
				    envelope.play(osc, 0, 0.1);
				}
			}
			p.draw = () => {
				p.background(255)
				for (var i = locs.length - 1; i >= 0; i--) {
					var h = calcVec( locs[i].x - p.mouseX, locs[i].y - p.mouseY);
					p.bezier(
						locs[i].x, 
						locs[i].y, 
						locs[i].x + 15*p.cos(h.heading()), 
						locs[i].x + 15*p.sin(h.heading()),
						locs[i].x + 15*p.sin(h.heading()),
						locs[i].y + 15*p.cos(h.heading()),
						locs[i].y,
						locs[i].x
					);
				};
			}

			function calcVec(x, y) {
				return new p5.Vector(y - x, - x - y);
			}			
